// jquery.shake // see http://github.com/pklink/jquery.shake or http://github.com/maxbublik/simpleshake
(function($){'use strict';$.fn.extend({shake:function(options){var defaults={amplitude:4,iterations:2,speed:45};var options=$.extend(defaults,options);return this.each(function(){var a=options.amplitude;for(var i=0;i<(options.iterations*2);i++){a=-a;$(this).animate({'margin-left':"+="+a+'px','margin-right':"+="+a+'px'},options.speed)}})}})})(jQuery);